import React, {Component} from 'react';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import Home from './components/Home';
import UserProfile from './components/UserProfile';
import Debits from './components/Debits';

class App extends Component {
  constructor(){
    super();
    this.state = {

      accountBalance: {
        credits: 5,
        debits: 2,
        total: 0
      },

      debits_json: NaN,
      credit_json: NaN,
      isLoaded: 0,

      currentUser: {
        userName: 'bob_loblaw',
        memberSince: '08/23/99'
      }
    }

    this.appendJson = this.appendJson.bind(this);
    this.UpdateTotal = this.UpdateTotal.bind(this);
    this.UpdateJsonData = this.UpdateJsonData.bind(this);
  }

  componentDidMount(){
    //Fetching Credit informaiton
    fetch('https://moj-api.herokuapp.com/credits')
    .then(response => response.json())
    .then(myJson => (this.setState({credit_json: myJson}, this.UpdateJsonData('credit'))));

    //Fetching Debit information
    fetch('https://moj-api.herokuapp.com/debits')
    .then(response => response.json())
    .then(myJson => (this.setState({debits_json: myJson},console.log(myJson),this.UpdateJsonData('debit'))));

  }



  //Functions for Manipulating States
  appendJson = (whatJson, item) => {
    //Debits
    if(whatJson === 'debit'){
      this.setState({
        debit: this.state.debit.push(item)
      });
    }
    //Credits
    if(whatJson === 'credit'){
      this.setState({
        credit: this.state.credit.push(item)
      });
    }
  } //End appendJson

  //Calculating total amount
  UpdateTotal = () => {
    let currCredit = this.state.accountBalance.credits;
    let currDebit = this.state.accountBalance.debits;
    this.setState({
      accountBalance: {
        total: currDebit - currCredit
      }
    });
  }

  //Adding Debits
  UpdateJsonData = (whatJson) =>{
    let total = 0;


    if(whatJson === 'debit'){
      console.log(this.state.debits_json);
      this.state.debits_json.forEach(element => {
        total += element["amount"];
      });
    }
    //Credits
    else if(whatJson === 'credit'){
      console.log(this.state.credits_json);
      this.state.credit_json.forEach(element => {
        total += element["amount"];
      });
    }


    //Update state
    this.setState({
      accountBalance: {
        debits: total
      }
    }, this.UpdateTotal);
  }

  render() {
    console.log(this.state.debits_json);
      const HomeComponent = () => (<Home accountBalance={this.state.accountBalance}/>);
      const UserProfileComponent = () => (<UserProfile userInfo={this.state.currentUser}/>);
      const DebitsComponent = () => (<Debits accountBalance={this.state.accountBalance} appendJson={this.appendJson.bind(this)}/>);

      return (
          <Router>
            <div>
              <Route exact path='/' render={HomeComponent}/>
              <Route exact path='/userProfile' render={UserProfileComponent} />
              <Route exact path='/Debits' render={DebitsComponent} />
            </div>
          </Router>
      );

  } //End Render
}

export default App;
