import React, {Component} from 'react';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import Home from './components/Home';
import UserProfile from './components/UserProfile';
import Debits from './components/Debits';
import axios from 'axios';

class App extends Component {
  constructor(){
    super();
    this.state = {

      accountBalance: 0,
      debitAmount: 0,
      debitHistory: [],

      creditAmount: 0,
      creditHistory: [],

      currentUser: {
        userName: 'bob_loblaw',
        memberSince: '08/23/99'
      }
    }
    //Binding
    this.UpdateDebit = this.UpdateDebit.bind(this);
    this.UpdateBalance = this.UpdateBalance.bind(this);
    this.getDebits = this.getDebits.bind(this);
    this.getCredits = this.getCredits.bind(this);
  }

  componentDidMount(){
    this.getDebits();
    this.getCredits();

    /*
    //Fetching Credit informaiton
    fetch('https://moj-api.herokuapp.com/credits')
    .then(response => response.json())
    .then(myJson => (
      this.setState({
        credit_json: myJson
      },console.log('Credit '),console.log(this.state.credits_json))));

    //Fetching Debit information
    fetch('https://moj-api.herokuapp.com/debits')
    .then(response => response.json())
    .then(myJson => (
      this.setState({
        debits_json: myJson
      },console.log('Debit '),console.log(this.state.credits_json))));
      */
  }

  getDebits(){
    axios.get("https://moj-api.herokuapp.com/debits")
    .then(response => {
      let result = response.data;
      this.setState({debitHistory: result});
      //this.calculateDebit();
    });
  }
  getCredits(){
    axios.get("https://moj-api.herokuapp.com/debits")
    .then(response => {
      let result = response.data;
      this.setState({creditHistory: result});
      //this.calculateDebit();
    });
  }



  UpdateBalance = () =>{
    this.setState({
      accountBalance: this.state.creditAmount - this.state.debitAmount
    });
    console.log(this.state.accountBalance);
  }

  UpdateDebit = (value) => {
    this.setState({
      debitAmount: value
    });
    this.UpdateBalance();
  }


  render() {
      const HomeComponent = () => (<Home accountBalance={this.state.accountBalance}/>);
      const UserProfileComponent = () => (<UserProfile userInfo={this.state.currentUser}/>);
      const DebitsComponent = () => (<Debits accountBalance={this.state.accountBalance} UpdateDebit={this.UpdateDebit.bind(this)} debitHistory={this.state.debitHistory}/>);

      return (
          <Router>
            <div>
              <Route exact path='/' render={HomeComponent}/>
              <Route exact path='/userProfile' render={UserProfileComponent} />
              <Route exact path='/Debits' render={DebitsComponent} />
            </div>
          </Router>
      );

  } //End Render
}

export default App;
