import React, {Component} from 'react';

import {Link} from 'react-router-dom';

class Debits extends Component {

  constructor(props){
    super(props);

    this.state = {
        data: this.props.debitHistory || []
    }


    this.getDebit = this.getDebit.bind(this);
    this.addDebit = this.addDebit.bind(this);
  }



  //Obtaining the Debit
  getDebit = () => {
    let total = 0;
    this.state.data.forEach(element => {
      total += element["amount"];
    });
    console.log(total);
    return total;
  }

  addDebit = () => {
    var test = this.state.data;

    let newObject = {
      amount: 250,
      date: new Date(),
      description: 'NEW DEBIT',
      id: 2
    };

    test.push(newObject);
    this.props.UpdateDebit(test, newObject["amount"]);


  }

  render(){
    let staticElement = (
      <div>
        <h1>Debit Information</h1>
        <Link to="/">Home</Link>
      </div>
    );

    return(
      <div>
        {staticElement}
        <button onClick={this.addDebit}>Add Debit</button>
        {
          this.state.data.map((info) =>
            <div className="DebitEntry">
              <h3>{info["description"]}</h3>
              <ul className="des">
                <li>Amount: {info["amount"]}</li>
                <li>Date: {info["date"]}</li>
              </ul>
              <hr/>
            </div>
          )
        }
          </div>
      );
  }

}

export default Debits;
