import React, {Component} from 'react';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import Home from './components/Home';
import UserProfile from './components/UserProfile';
import Debits from './components/Debits';

class App extends Component {
  constructor(){
    super();
    this.state = {

      accountBalance: 0,
      debitAmount: 0,
      creditAmount: 0,

      currentUser: {
        userName: 'bob_loblaw',
        memberSince: '08/23/99'
      }
    }
    //Binding
    this.UpdateDebit = this.UpdateDebit.bind(this);
  }

  componentDidMount(){
    /*
    //Fetching Credit informaiton
    fetch('https://moj-api.herokuapp.com/credits')
    .then(response => response.json())
    .then(myJson => (
      this.setState({
        credit_json: myJson
      },console.log('Credit '),console.log(this.state.credits_json))));

    //Fetching Debit information
    fetch('https://moj-api.herokuapp.com/debits')
    .then(response => response.json())
    .then(myJson => (
      this.setState({
        debits_json: myJson
      },console.log('Debit '),console.log(this.state.credits_json))));
      */
  }

  UpdateBalance = () =>{
    this.setState({
      accountBalance: this.state.creditAmount - this.state.debitAmount
    });
  }
  UpdateDebit = (value) => {
    this.setState({
      debitAmount: value
    }, this.UpdateBalance);
    console.log(this.state.debitAmount);
  }


  render() {
      const HomeComponent = () => (<Home accountBalance={this.state.accountBalance}/>);
      const UserProfileComponent = () => (<UserProfile userInfo={this.state.currentUser}/>);
      const DebitsComponent = () => (<Debits accountBalance={this.state.accountBalance} UpdateDebit={this.UpdateDebit.bind(this)}/>);

      return (
          <Router>
            <div>
              <Route exact path='/' render={HomeComponent}/>
              <Route exact path='/userProfile' render={UserProfileComponent} />
              <Route exact path='/Debits' render={DebitsComponent} />
            </div>
          </Router>
      );

  } //End Render
}

export default App;
