import React, {Component} from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom';

class Debits extends Component {

    constructor(props){
        console.log("here");
        super(props);
        this.state = {
            data: []
        }
        this.fetchDebitData = this.fetchDebitData.bind(this);

    }
    componentDidMount(){
        this.fetchDebitData();
        this.interval = setInterval(() => this.fetchDebitData(),60*1000);
    }
    fetchDebitData(){
        axios.get("https://moj-api.herokuapp.com/debits")
        .then(response => {
            var result = response.data;
            this.setState({data: result});
            console.log(result);
            console.log("here");
        })
        .catch(err => console.log(err));
    }
    render(){
        return(
            <div>
                <h1>DEBITS</h1>
                <Link to="/">Home</Link>
                {this.state.data.map((info) =>
                    <div className="deb">
                        <h3 className="dat">Timestamp: {info["date"]}</h3>
                        <ul className="des">
                            <li>Description: {info["description"]}</li>
                            <li>Amount: {info["amount"]}</li>
                            <li>Type: Debit</li>
                        </ul>
                    </div>
                )}
            </div>
        );
    }

}

export default Debits;
